function App() {
  const [movies, setMovies] = useState([]);
  const [displayedMovies, setDisplayedMovies] = useState([]);

  useEffect(() => {
    fetch("http://localhost:5000/movies")
      .then(res => res.json())
      .then(data => {
        setMovies(data);
        setDisplayedMovies(data); // show all movies initially
      });
  }, []);

  const filterByGenre = (genre) => {
    if (genre === "All") {
      setDisplayedMovies(movies);
    } else {
      const filtered = movies.filter(movie => movie.genre === genre);
      setDisplayedMovies(filtered);
    }
  };

  const searchMovies = (query) => {
    const filtered = movies.filter(movie =>
      movie.title.toLowerCase().includes(query.toLowerCase())
    );
    setDisplayedMovies(filtered);
  };

  return (
    <div style={{ background: "black", minHeight: "100vh", paddingTop: "20px" }}>
      <h1 style={{ color: "white", textAlign: "center" }}>
        ðŸŽ¥ Movie Recommendation App
      </h1>

      {/* You can pass displayedMovies, filterByGenre, searchMovies to MovieList */}
      <MovieList
        movies={displayedMovies}
        filterByGenre={filterByGenre}
        searchMovies={searchMovies}
      />
    </div>
  );
}

export default App;
