import React, { useEffect, useState } from "react";

function App() {
  const [movies, setMovies] = useState([]);

  // Fetch movies from your backend API
  useEffect(() => {
    fetch("http://localhost:5000/movies")
      .then(res => res.json())
      .then(data => setMovies(data))
      .catch(err => console.error(err));
  }, []);

  return (
    <div style={styles.container}>
      <h1 style={styles.title}>üé• Movie Recommendation App</h1>

      {movies.length === 0 ? (
        <p style={styles.loading}>Loading movies...</p>
      ) : (
        <div style={styles.grid}>
          {movies.map((movie, index) => (
            <div key={index} style={styles.card}>
              <img
                src={movie.poster}
                alt={movie.title}
                style={styles.image}
              />
              <h3>{movie.title}</h3>
              <p>{movie.genre}</p>
              <p>‚≠ê {movie.rating}/10</p>
            </div>
          ))}
        </div>
      )}
    </div>
  );
}

const styles = {
  container: { textAlign: "center", padding: "20px", fontFamily: "Arial" },
  title: { fontSize: "40px", marginBottom: "20px" },
  loading: { fontSize: "20px", color: "gray" },
  grid: {
    display: "grid",
    gridTemplateColumns: "repeat(auto-fill, minmax(200px, 1fr))",
    gap: "20px",
    padding: "20px"
  },
  card: {
    background: "#fff",
    padding: "15px",
    borderRadius: "10px",
    boxShadow: "0px 2px 5px rgba(0,0,0,0.2)"
  },
  image: { width: "100%", borderRadius: "10px" }
};

export default App;
