import React, { useState, useEffect } from "react";
import MovieList from "./components/MovieList";
import Favorites from "./components/Favorites";

function App() {
  const [movies, setMovies] = useState([]);
  const [displayedMovies, setDisplayedMovies] = useState([]);
  const [favorites, setFavorites] = useState([]); // âœ… Move favorites here

  useEffect(() => {
    fetch("http://localhost:5000/movies")
      .then(res => res.json())
      .then(data => {
        setMovies(data);
        setDisplayedMovies(data);
      });
  }, []);

  const filterByGenre = (genre) => {
    if (genre === "All" || genre === "") {
      setDisplayedMovies(movies);
    } else {
      setDisplayedMovies(movies.filter(movie => movie.genre === genre));
    }
  };

  const searchMovies = (query) => {
    setDisplayedMovies(
      movies.filter(movie => movie.title.toLowerCase().includes(query.toLowerCase()))
    );
  };

  return (
    <div style={{ background: "black", minHeight: "100vh", paddingTop: "20px" }}>
      <h1 style={{ color: "white", textAlign: "center" }}>ðŸŽ¥ Movie Recommendation App</h1>

      <MovieList
        movies={displayedMovies}
        searchMovies={searchMovies}
        filterByGenre={filterByGenre}
        favorites={favorites}          // âœ… pass favorites
        setFavorites={setFavorites}    // âœ… pass setFavorites
      />

      <Favorites
        favorites={favorites}
        setFavorites={setFavorites}
      />
    </div>
  );
}

export default App;
