import React from "react";

function Navbar({ favoritesCount }) {
  return (
    <nav style={styles.navbar}>
      <div style={styles.logo}>ðŸŽ¥ Movie Recommendation App</div>
      <div style={styles.links}>
        <a href="/" style={styles.link}>Home</a>
        <a href="/favorites" style={styles.link}>
          Favorites {favoritesCount > 0 && `(${favoritesCount})`}
        </a>
      </div>
    </nav>
  );
}

const styles = {
  navbar: {
    display: "flex",
    justifyContent: "space-between",
    alignItems: "center",
    backgroundColor: "#111",
    padding: "10px 20px",
    color: "white",
    position: "sticky",
    top: 0,
    zIndex: 100,
  },
  logo: {
    fontSize: "20px",
    fontWeight: "bold",
  },
  links: {
    display: "flex",
    gap: "15px",
  },
  link: {
    color: "white",
    textDecoration: "none",
    fontWeight: "bold",
    transition: "color 0.3s",
  },
};

export default Navbar;
