import React from "react";

function MovieCard({ movie }) {
  return (
    <div style={styles.card}>
      <img src={movie.poster} alt={movie.title} style={styles.image} />
      <h3 style={styles.title}>{movie.title}</h3>
      <p style={styles.genre}>{movie.genre}</p>
      <p style={styles.rating}>‚≠ê {movie.rating}</p>
    </div>
  );
}

const styles = {
  card: {
    background: "#1e1e1e",
    color: "white",
    borderRadius: "10px",
    width: "200px",
    margin: "10px",
    padding: "10px",
    textAlign: "center",
    boxShadow: "0 4px 8px rgba(0,0,0,0.5)",
    transition: "transform 0.2s",
  },
  image: {
    width: "100%",
    height: "300px",
    objectFit: "cover",
    borderRadius: "10px",
  },
  title: {
    fontSize: "18px",
    margin: "10px 0 5px 0",
  },
  genre: {
    fontSize: "14px",
    color: "#ccc",
    margin: "0",
  },
  rating: {
    fontSize: "14px",
    color: "#ffd700",
    margin: "5px 0 0 0",
  },
};

export default MovieCard;
