import React, { useState } from "react";
import MovieList from "./components/MovieList";
import Favorites from "./components/Favorites"; // Make sure Favorites.js exists
import { BrowserRouter as Router, Routes, Route, Link } from "react-router-dom";

function App() {
  const [favorites, setFavorites] = useState([]);
  const [movies, setMovies] = useState([]); // original movies from backend
  const [displayedMovies, setDisplayedMovies] = useState([]); // movies shown after search/filter

  return (
    <Router>
      <div style={{ background: "black", minHeight: "100vh", paddingTop: "20px" }}>
        <nav style={{ textAlign: "center", marginBottom: "20px" }}>
          <Link to="/" style={{ color: "white", margin: "0 10px" }}>Home</Link>
          <Link to="/favorites" style={{ color: "white", margin: "0 10px" }}>Favorites</Link>
        </nav>

        <Routes>
          <Route path="/" element={<MovieList favorites={favorites} setFavorites={setFavorites} />} />
          <Route path="/favorites" element={<Favorites favorites={favorites} setFavorites={setFavorites} />} />
        </Routes>
      </div>
    </Router>
  );
}
useEffect(() => {
  fetch("http://localhost:5000/movies")
    .then(res => res.json())
    .then(data => {
      setMovies(data);
      setDisplayedMovies(data); // initially show all movies
    });
}, []);
const filterByGenre = (genre) => {
  if (genre === "All") {
    setDisplayedMovies(movies); // show all
  } else {
    const filtered = movies.filter(movie => movie.genre === genre);
    setDisplayedMovies(filtered);
  }
};


export default App;
