import React from "react";
import "./MovieCard.css";

function MovieCard({ movie }) {
  const handleFavorite = () => {
    // Get current favorites from localStorage
    const currentFavs = JSON.parse(localStorage.getItem("favorites")) || [];

    // Check if movie is already in favorites
    const isAlreadyFav = currentFavs.some(m => m.title === movie.title);

    let updatedFavs;
    if (isAlreadyFav) {
      // Remove from favorites
      updatedFavs = currentFavs.filter(m => m.title !== movie.title);
    } else {
      // Add to favorites
      updatedFavs = [...currentFavs, movie];
    }

    localStorage.setItem("favorites", JSON.stringify(updatedFavs));
    alert(isAlreadyFav ? "Removed from Favorites!" : "Added to Favorites!");
  };

  return (
    <div className="movie-card">
      <img src={movie.poster} alt={movie.title} className="movie-poster" />
      <h3 className="movie-title">{movie.title}</h3>
      <p className="movie-genre">{movie.genre}</p>
      <p className="movie-rating">⭐ {movie.rating}</p>
      <p className="movie-description">{movie.description}</p>
      <p className="movie-director"><b>Director:</b> {movie.director}</p>
      <p className="movie-cast"><b>Cast:</b> {movie.cast.join(", ")}</p>
      <button className="favorite-btn" onClick={handleFavorite}>
        ❤️ Add / Remove Favorite
      </button>
    </div>
  );
}

export default MovieCard;
