import React, { useEffect, useState } from "react";
import MovieCard from "./MovieCard";

function MovieList({ favorites, setFavorites }) {
  const [movies, setMovies] = useState([]);
  const [loading, setLoading] = useState(true);
  const [search, setSearch] = useState("");
  const [genreFilter, setGenreFilter] = useState("All");

  useEffect(() => {
    fetch("http://localhost:5000/movies")
      .then((res) => res.json())
      .then((data) => {
        setMovies(data);
        setLoading(false);
      });
  }, []);

  const filteredMovies = movies.filter((movie) => {
    const matchesSearch = movie.title.toLowerCase().includes(search.toLowerCase());
    const matchesGenre = genreFilter === "All" || movie.genre === movie.genre;
    return matchesSearch && matchesGenre;
  });

  const genres = ["All", ...new Set(movies.map((m) => m.genre))];

  return (
    <div style={styles.container}>
      <h2>ðŸŽ¬ Recommended Movies</h2>

      {/* Search and Filter */}
      <div style={{ marginBottom: "20px" }}>
        <input
          type="text"
          placeholder="Search by title..."
          value={search}
          onChange={(e) => setSearch(e.target.value)}
          style={styles.input}
        />
        <select
          value={genreFilter}
          onChange={(e) => setGenreFilter(e.target.value)}
          style={styles.input}
        >
          {genres.map((g) => (
            <option key={g} value={g}>{g}</option>
          ))}
        </select>
      </div>

      {loading ? (
        <p>Loading...</p>
      ) : (
        <div style={styles.grid}>
          {filteredMovies.map((movie) => (
            <MovieCard key={movie.title} movie={movie} favorites={favorites} setFavorites={setFavorites} />
          ))}
        </div>
      )}
    </div>
  );
}

const styles = {
  container: { textAlign: "center", padding: "20px", color: "white" },
  grid: { display: "flex", justifyContent: "center", flexWrap: "wrap", marginTop: "20px" },
  input: { padding: "5px 10px", margin: "0 10px", borderRadius: "4px" },
};

export default MovieList;
