import React, { useEffect, useState } from "react";

function App() {
  const [movies, setMovies] = useState([]);
  const [loading, setLoading] = useState(true);

  useEffect(() => {
    fetch("http://localhost:5000/movies")
      .then((res) => res.json())
      .then((data) => {
        setMovies(data);
        setLoading(false);
      })
      .catch((error) => {
        console.error("Error fetching movies:", error);
        setLoading(false);
      });
  }, []);

  return (
    <div style={{ padding: "20px", fontFamily: "Arial" }}>
      <h1 style={{ textAlign: "center" }}>üé• Movie Recommendation App</h1>

      {loading ? (
        <p style={{ textAlign: "center" }}>Loading movies...</p>
      ) : (
        <div
          style={{
            display: "grid",
            gridTemplateColumns: "repeat(auto-fill, minmax(250px, 1fr))",
            gap: "20px",
            marginTop: "20px"
          }}
        >
          {movies.map((movie, index) => (
            <div
              key={index}
              style={{
                border: "1px solid #ddd",
                borderRadius: "10px",
                padding: "15px",
                textAlign: "center",
                boxShadow: "0px 4px 10px rgba(0,0,0,0.1)"
              }}
            >
              <img
                src={movie.poster}
                alt={movie.title}
                style={{
                  width: "100%",
                  height: "350px",
                  borderRadius: "10px",
                  objectFit: "cover"
                }}
              />
              <h2>{movie.title}</h2>
              <p>üé≠ Genre: {movie.genre}</p>
              <p>‚≠ê Rating: {movie.rating}</p>
            </div>
          ))}
        </div>
      )}
    </div>
  );
}

export default App;
